import React, { useEffect, useState } from 'react';

const ParallaxWebsite = () => {
    const [scrollValues, setScrollValues] = useState({
        marginTop: 0,
        mountainBottom: 106,
        leftCloudMargin: 0,
        mainCloudMargin: 0,
        rightCloudMargin: 0,
    });

    useEffect(() => {
        const handleScroll = () => {
            setScrollValues({
                marginTop: `${window.scrollY * 1.5}px`,
                mountainBottom: 106 - window.scrollY,
                leftCloudMargin: `-${window.scrollY}px`,
                mainCloudMargin: `-${window.scrollY}px`,
                rightCloudMargin: `-${window.scrollY}px`,
            });
        };

        window.addEventListener("scroll", handleScroll);

        // Clean up the event listener when the component unmounts
        return () => {
            window.removeEventListener("scroll", handleScroll);
        };
    }, []); // Empty dependency array means this effect runs once after the initial render

    return (
        <div className="main">
            <header>
                <nav>
                    <span>
                        <a href="#" className="active">Home</a>
                        <a href="#">About</a>
                        <a href="#">Contact</a>
                        <a href="#">Login</a>
                    </span>
                </nav>
            </header>

            <section id="Home">
                <h1 style={{ marginTop: scrollValues.marginTop }}>THURVOX WEBSITE</h1>

                <img src="/images/leftCloud.png" id="leftCloud" alt="" style={{ marginLeft: scrollValues.leftCloudMargin }} />
                <img src="/images/mainCloud.png" id="mainCloud" alt="" style={{ marginTop: scrollValues.mainCloudMargin }} />
                <img src="/images/rightCloud.png" id="rightCloud" alt="" style={{ marginRight: scrollValues.rightCloudMargin }} />

                <img src="/images/Layer 1.png" id="Mountain1" alt="" style={{ marginBottom: scrollValues.mountainBottom }} />
                <img src="/images/Layer 2.png" id="Mountain2" alt="" />
            </section>
            <section id="About">
                <h1>About</h1>
                <p>Lorem ipsum ...</p>
                {/* Repeat the above paragraph as needed */}
            </section>
        </div>
    );
};

export default ParallaxWebsite;
