import React, { useEffect, useRef } from 'react';
import logo from './logo.svg';
import './App.css';

function ParallaxWebsite() {
  const mainRef = useRef(null); // Reference to the main container element

  useEffect(() => {
    const updateParallax = () => {
      const scrollY = window.scrollY;

      // Optimized parallax calculations for smoother movement
      const homeH1marginTop = scrollY * 0.75; // Apply a more subtle parallax effect
      const mountain1marginBottom = 106 - scrollY * 0.25; // Reduce movement for a more grounded appearance
      const cloudMargin = scrollY * 0.2; // Use the same factor for consistent cloud movement

      document.querySelector('#Home h1').style.marginTop = `${homeH1marginTop}px`;
      document.querySelector('#Mountain1').style.marginBottom = `${mountain1marginBottom}px`;
      document.querySelector('#leftCloud').style.marginLeft = `-${cloudMargin}px`;
      document.querySelector('#mainCloud').style.marginTop = `-${cloudMargin}px`;
      document.querySelector('#rightCloud').style.marginRight = `-${cloudMargin}px`;
    };

    // Add the event listener only once, on component mount
    window.addEventListener('scroll', updateParallax);

    // Remove the event listener on component unmount to avoid memory leaks
    return () => window.removeEventListener('scroll', updateParallax);
  }, []); // Run the useEffect hook only once on component mount

  return (
    <div className="main" ref={mainRef}>
      <header>
        <nav>
        <span>
            <a href="#" className="active">Home</a>
            <a href="#">Premium</a>
            <a href="#">Contact Us</a>
            <a href="#">About Us</a>
                    </span>
          {/* ... navigation */}
        </nav>
      </header>

      <section id="Home">
        <h1 className='h1name fade-in'>THURVOX WEBSITE</h1>
        <h2 className="small-text">
RIDE THE DIGITAL WAVE WITH THURUVOX, THE ULTIMATE ONLINE HOTSPOT FOR PDF TRANSLATIONS.
<br />BEHOLD THE CHARM OF REAL-TIME PREVIEWS AND EDITS.  <br />STRAP IN FOR AN INTERACTIVE ADVENTURE THAT'S AS FUN AS IT IS FUNCTIONAL. 
</h2>
<div></div>
        <img src="/images/leftCloud.png" id="leftCloud" alt="" />
        <img src="/images/mainCloud.png" id="mainCloud" alt="" />
        <img src="/images/rightCloud.png" id="rightCloud" alt="" />

        <img src="/images/Layer 1.png" id="Mountain1" alt="" />
        <img src="/images/Layer 2.png" id="Mountain2" alt="" />
      </section>

      <section id="About">
        <h1>About</h1>
        <p>Lorem ipsum ...</p>
        {/* ... */}
      </section>

      {/* Other sections */}
    </div>
  );
}

export default ParallaxWebsite;
