import React, { useEffect, useRef, useState } from 'react';
import './App.css';

function ParallaxWebsite() {
  const mainRef = useRef(null); // Reference to the main container element
  const [isH1Visible, setIsH1Visible] = useState(false);

  useEffect(() => {
    const updateParallax = () => {
      const scrollY = window.scrollY;

      // Your existing parallax calculations

      // Check if the h1 element is in the viewport
      const h1Rect = document.querySelector('.h1name').getBoundingClientRect();
      const h1Visible = h1Rect.top <= window.innerHeight && h1Rect.bottom >= 0;

      // Set the state to trigger the fade-in effect
      if (h1Visible && !isH1Visible) {
        setIsH1Visible(true);
      }
    };

    // Add the event listener only once, on component mount
    window.addEventListener('scroll', updateParallax);

    // Remove the event listener on component unmount to avoid memory leaks
    return () => window.removeEventListener('scroll', updateParallax);
  }, [isH1Visible]); // Include isH1Visible in the dependency array to avoid closure issues

  return (
    <div className="main" ref={mainRef}>
      <header>
        <nav>
          {/* ... navigation */}
        </nav>
      </header>

      <section id="Home">
        {/* Add fade-in class based on isH1Visible state */}
        <h1 className={`h1name ${isH1Visible ? 'fade-in' : ''}`}>THURVOX WEBSITE</h1>

        {/* Your existing images */}
      </section>

      <section id="About">
        <h1>About</h1>
        <p>Lorem ipsum ...</p>
        {/* ... */}
      </section>

      {/* Other sections */}
    </div>
  );
}

export default ParallaxWebsite;
