import React, { useEffect, useState } from 'react';

const ParallaxWebsite = () => {
    const [marginTop, setMarginTop] = useState(0);
    const [mountainBottom, setMountainBottom] = useState(106);
    const [leftCloudMargin, setLeftCloudMargin] = useState(0);
    const [mainCloudMargin, setMainCloudMargin] = useState(0);
    const [rightCloudMargin, setRightCloudMargin] = useState(0);

    useEffect(() => {
        const handleScroll = () => {
            setMarginTop(`${window.scrollY * 1.5}px`);
            setMountainBottom(106 - window.scrollY);
            setLeftCloudMargin(`-${window.scrollY}px`);
            setMainCloudMargin(`-${window.scrollY}px`);
            setRightCloudMargin(`-${window.scrollY}px`);
        };

        window.addEventListener("scroll", handleScroll);

        // Clean up the event listener when the component unmounts
        return () => {
            window.removeEventListener("scroll", handleScroll);
        };
    }, []); // Empty dependency array means this effect runs once after the initial render

    return (
        <div className="main">
            <header>
                <nav>
                    <span>
                        <a href="#" className="active">Home</a>
                        <a href="#">About</a>
                        <a href="#">Contact</a>
                        <a href="#">Login</a>
                    </span>
                </nav>
            </header>

            <section id="Home">
                <h1 style={{ marginTop }}>THURVOX WEBSITE</h1>

                <img src="/images/leftCloud.png" id="leftCloud" alt="" style={{ marginLeft: leftCloudMargin }} />
                <img src="/images/mainCloud.png" id="mainCloud" alt="" style={{ marginTop: mainCloudMargin }} />
                <img src="/images/rightCloud.png" id="rightCloud" alt="" style={{ marginRight: rightCloudMargin }} />

                <img src="/images/Layer 1.png" id="Mountain1" alt="" style={{ marginBottom: mountainBottom }} />
                <img src="/images/Layer 2.png" id="Mountain2" alt="" />
            </section>
            <section id="About">
                <h1>About</h1>
                <p>Lorem ipsum ...</p>
                {/* Repeat the above paragraph as needed */}
            </section>
        </div>
    );
};

export default ParallaxWebsite;
