import React, { useEffect, useRef } from 'react';
import logo from './logo.svg';
import './App.css';

function ParallaxWebsite() {
  const h1Ref = useRef(null); // Reference to the h1 element

  useEffect(() => {
    // Optimized parallax calculations for smoother movement
    const updateParallax = () => {
      const scrollY = window.scrollY;

      const homeH1marginTop = scrollY * 0.75; // Subtle parallax effect
      const mountain1marginBottom = 106 - scrollY * 0.25; // Grounded appearance
      const cloudMargin = scrollY * 0.2; // Consistent cloud movement

      document.querySelector('#Home h1').style.marginTop = `${homeH1marginTop}px`;
      document.querySelector('#Mountain1').style.marginBottom = `${mountain1marginBottom}px`;
      document.querySelector('#leftCloud').style.marginLeft = `-${cloudMargin}px`;
      document.querySelector('#mainCloud').style.marginTop = `-${cloudMargin}px`;
      document.querySelector('#rightCloud').style.marginRight = `-${cloudMargin}px`;
    };

    // Add parallax event listener once
    window.addEventListener('scroll', updateParallax);

    // Remove parallax listener on unmount
    return () => window.removeEventListener('scroll', updateParallax);
  }, []);

  useEffect(() => {
    // Fade in h1name upon entering the viewport
    const observer = new IntersectionObserver(
      (entries) => {
        if (entries[0].isIntersecting) {
          h1Ref.current.classList.add('fade-in');
        } else {
          h1Ref.current.classList.remove('fade-in');
        }
      },
      {
        threshold: 0.5, // Fade in when 50% of h1name is visible
        rootMargin: '50px', // Adjust based on element position and spacing
      }
    );
    observer.observe(h1Ref.current); // Observe the h1 element

    // Clean up observer on unmount to avoid memory leaks
    return () => observer.disconnect();
  }, []);

  return (
    <div className="main">
      <header>
        <nav>
          <span>
            <a href="#" className="active">Home</a>
            <a href="#">About</a>
            <a href="#">Contact</a>
            <a href="#">Login</a>
          </span>
          {/* ... navigation */}
        </nav>
      </header>

      <section id="Home">
        <h1 className="h1name fade-in" ref={h1Ref}>THURVOX WEBSITE</h1>

        {/* ... other elements */}
      </section>

      <section id="About">
        <h1>About</h1>
        <p>Lorem ipsum ...</p>
        {/* ... */}
      </section>

      {/* Other sections */}
    </div>
  );
}

export default ParallaxWebsite;
